import {UseAuthContext} from '../hooks/UseAuthContext'


const Account = () => {
    const {user} = UseAuthContext()

    return ( 
        <div className="container border">
            <h5 className="text-end pt-3 pe-3">حساب کاربری من </h5>
            <p className='text-end lead mt-3'><span>{user.name}</span> : نام  </p>
            <p className='text-end lead'> <span>{user.lastname}</span> : نام خانوادگی </p>
            <p className='text-end lead'> <span className='font-weight-bold'>{user.email}</span> : ایمیل </p>
            <button className='btn btn-outline-primary btn-lg fw-btn my-5'>ویرایش حساب کاربری</button>
        </div>
     );
}
export default Account;
