import { useState } from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { useEffect } from "react";
import {UseAuthContext} from '../hooks/UseAuthContext'

const MyProducts = () => {
    
    const {user} = UseAuthContext()
    const [error,setError] = useState(null)
    const [products,setProducts] = useState(null)
    const [ischanging,setIschanging] = useState(false)

    const RemoveProduct = () => {

    }
    const UpdateProduct = () => {

    }

    useEffect(()=>{
        const userProducts = async () => {
            const response = await fetch('/api/products/userproducts',{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if (!response.ok){
                setError(json.error)
            }
            if (response.ok){
                setProducts(json)
            }
            
        }
        if(user){
            userProducts()
        }
           
    },[user])
    return ( 
        <div>
            <h5 className="text-end pt-3 pe-3">محصولات های من </h5>
            <div className="myprojects">
                {!ischanging && (
                    <div className="container border">
                    
                    {error && <div>{error}</div>}
                    {products && products.map((p)=>(
                        <div className="row justify-content-center" key={p._id}>
                            <div className="col-12 col-lg-8 my-3 ">
                                <div className="card">
                                    <div className="card-header">{p.title}</div>
                                    <div className="card-body">
                                        <div className="card-title">{p.user_id}</div>
                                            <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                            <div className="card-text">{p.about} </div>
                                            <div className="mt-3">
                                                <button onClick={RemoveProduct} className="btn btn-danger me-2">Reomve</button>
                                                <button onClick={UpdateProduct} className="btn btn-warning">Update</button>
                                            </div>
        
                                        </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
                )}
            </div>
        </div>
     );
}
 
export default MyProducts;
