import { useState , useRef } from "react"
import { UseEditProduct } from "../hooks/useEditProduct"


const Edit = ({id}) => {

    const [title,setTitle] = useState('')
    const [price,setPrice] = useState()
    const [number,setNumber] = useState()
    const [instagram,setInstagram] = useState('')
    const [telegram,setTelegram] = useState('')
    const [category,setCategory] = useState('ورزش')
    const [about,setAbout] = useState('')
    const [image,setImage] = useState(null)
    const [preview,setPreview] = useState()
    const fileInputRef = useRef()

    const {error,isloading,EditProduct} = UseEditProduct()

    const submitHandler = async (e) => {
        e.preventDefault()
        // let updates = []
        // if(title){
        //     updates.push({title})
        // }
        // if(price){
        //     updates.push({price})
        // }
        // if(number){
        //     updates.push({number})
        // }
        // if(instagram){
        //     updates.push({instagram})
        // }
        // if(category){
        //     updates.push({category})
        // }
        // if(about){
        //     updates.push({about})
        // }
        
        // console.log(updates)
         await EditProduct(id,title,about,price,category,number,instagram,telegram)
    }

    return ( 
        
        <div className="container border">
            <h5 className="text-end pt-3 pe-3">ویرایش</h5>
            <form onSubmit={submitHandler} className="from-controller product-form">
                <div className="text-end">
                    <label className="form-label lead ">عنوان جدید</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                         <i className="bi bi-pen-fill"></i>
                    </span>
                    <input type="text"
                    
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="form-control text-end " />
                </div>
                <div className="text-end">
                    <label className="form-label lead my-2">قیمت (به تومان)</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                        <i className="bi bi-tag-fill"></i>
                    </span>
                    <input type="number"
                     
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control text-end" 
                    placeholder="به تومان وارد کنید" />
                </div>
                <div className="text-end">
                    <label className="form-label lead my-2">شماره تماس</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                        <i className="bi bi-telephone-minus"></i>
                    </span>
                    <input type="number" 
                    
                    value={number}
                    onChange={(e) => setNumber(e.target.value)}
                    className="form-control text-end"  />
                </div>

                <div className="text-end">
                    <label className="form-label lead my-2">آیدی اینستاگرام</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                        <i className="bi bi-instagram"></i>
                    </span>
                    <input type="text" 
                    
                    value={instagram}
                    onChange={(e) => setInstagram(e.target.value)}
                    className="form-control text-end" 
                    placeholder="آیدی اینستاگرام خود را بدون @ وارد کنید" />
                </div>
                <div className="text-end">
                    <label className="form-label lead my-2">آیدی تلگرام</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                        <i className="bi bi-telegram"></i>
                    </span>
                    <input type="text" 
                    
                    value={telegram}
                    onChange={(e) => setTelegram(e.target.value)}
                    className="form-control text-end" 
                    placeholder="یوزرنیم تلگرام خود را بدون @ وارد کنید" />
                </div>
                <div className="text-end">
                    <label className="form-label lead my-2">دسته بندی</label>
                </div>
                <div className="input-group">
                    <span className="input-group-text">
                         <i className="bi bi-bookmark-check-fill"></i>
                    </span>
                    <select 
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    class="form-select text-end"
                     aria-label="Default select example">
                        <option value ="ورزش" selected>ورزش</option>
                        <option value="سریال">سریال</option>
                        <option value="فیلم">فیلم</option>
                        <option value="بازی">بازی</option>
                    </select>
                </div>

                <div className="text-end">
                    <label className="form-label lead my-2">توضیحات بیشتر</label>
                </div>
                <textarea 
                    
                    value={about}
                    onChange={(e) => setAbout(e.target.value)}
                    class="form-control text-end" rows="3"></textarea>
               <div className="text-end mt-2">
                    <label className="form-label lead">عکس محصول</label>
                </div>
                    <div className="text-center">
                        
                        {preview ? 
                            <img src={preview} style={{width : "300px" , height : "300px" }}/>
                        :
                            <button 
                            onClick={(e) => {
                                e.preventDefault()
                                fileInputRef.current.click()
                            }}
                            className="addImage">
                                Add Image
                            </button>
                        }   
                        

                    </div>

                    <input 
                    
                    accept="image/*"
                    onChange={(e) => {
                        const file = e.target.files[0]
                        if(file && file.type.substr(0,5) === "image" ){
                            if(e.target.files[0].size > 2097152){
                                alert("File is too big!");
                            }else{
                                setImage(file)
                            }
                        }
                    }}
                    type="file"
                    ref={fileInputRef} 
                    style={{display :"none"}}
                    ></input>
       

                <button disabled={isloading} className="btn btn-outline-primary my-4 fw-btn">{isloading ? 'در حال ثبت' : ".ویرایش محصول"}</button>
                {error && <div className="bg-dark text-primary">{error}</div>}
            </form>
        </div>
        
     );
}
 
export default Edit;
