import { useState ,useEffect } from "react"
import { UseAuthContext } from "../hooks/UseAuthContext"
import Loading from "./Loading"
import {UseProductContext} from './UseProductContext'

import pic from '../assets/2.jpg';
import { Link } from "react-router-dom";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { useLike } from "../hooks/useLike";


const MyLikes = () => {


    const {user} = UseAuthContext()
    const [isloading,setIsloadig] = useState(true)
    const {likes,dispatch} = UseProductContext()
    const {error,addintresting,AddIntresting,RemoveIntresting,getUserLikes} = useLike()

    const removelike = async(id) => {
        await RemoveIntresting(id)
    }

    useEffect(()=>{
        const getUserLikes = async () => {
            const response = await fetch('/api/products/interests',{
                method : 'POST',
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
                })
                const json = await response.json()
                if (!response.ok){
                    setError(json.error)
                }
                if(response.ok){
                    setIsloadig(false)
                    dispatch({type:'SET_LIKES',payload:json})
    
                }
    
            
        }
        getUserLikes()
    },[])

    return ( 
        <div className="likes">
            <div className="container border border-primary">
                <h5 className="text-end pt-3 pe-3">علاقه مندی های من  </h5>
                {isloading && <Loading />}
            
                {!isloading && (
                    <div>
                        {error && <div>{error}</div>}
                        {likes && likes.map((p)=>(
                            <div className="row justify-content-center">
                                    <div className="col-12 col-md-8 my-3 " key={p._id}>
                                    <div className="card">
                                        <div className="card-header"><img src={pic} style={{width : "100%" , height : "100%"}} /></div>
                                        <div className="card-body">
                                            <div className="card-title text-end">{p.title}</div>
                                            <div className="card-subtitle text-end my-2">قیمت : {p.price} هزار تومان</div>
                                            <div className="card-text text-end mb-2">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>

                                            <div className="text-center">
                                                <button disabled={addintresting} 
                                                    onClick={() => removelike(p._id)} 
                                                    className="btn btn-outline-danger me-2 mb-2"> 
                                                    <i class="bi bi-heart-fill"></i>
                                                </button>
                                                <Link to={`/details/${p._id}`} className="btn btn-lg btn-secondary me-2">مشاهده محصول </Link>
                                            </div>
                                        {error && (
                                            <div className="bg-danger my-2">
                                                <p className="p-3 text-secondary lead">{error}</p>
                                            </div>
                                        )}

                                        </div>
                                    </div>
                                </div>
                            </div>
                                ))}
                    </div>

                    )}
                        
        
              

            </div>
        </div>

     );
}
 
export default MyLikes;
