import { useState } from "react"
import { UseAuthContext } from "./UseAuthContext"
import {UseProductContext} from './UseProductContext'

export const UseEditProduct = () => {
    const [error,setError] = useState(null)
    const [isloading,setIsloading] = useState(false)
    const {user} = UseAuthContext()
    

    const EditProduct = async (id , title = null,about = null ,price = null,category = null,number = null,instagram = null,telegram = null) => {
        setIsloading(true)
        setError(null)


        const response =  await fetch(`/api/products/${id}`,{
            method : 'PATCH',
            headers : {
                "Content-Type" : "application/json",
                'Authorization' : `Bearer ${user.token}`
            },
            body : JSON.stringify({title,about,price,category,number,instagram,telegram})
        })
        const json = await response.json()

        if (response.ok){
            setIsloading(false)      
        }
        if (!response.ok){
            setIsloading(false)
            setError(json.error)
        }
    }

    return {error,isloading,EditProduct}
}
