import { UseAuthContext } from "../hooks/UseAuthContext";
import { useState } from "react";

import { Navigate } from 'react-router-dom'

// import components

import Account from "../components/Account";
import AddProduct from "../components/AddProduct";
import Opininos from "../components/Opinions";
import Orders from "../components/Orders";
import MyLists from "../components/Mylists";
import Logout from "../components/Logout";


const PersonalPage = () => {
    const {user} = UseAuthContext()
    const [currentmode,setCurrentmode] = useState('AddProduct')


    return ( 
        <div className="personal">
            {user ? (                <div className="container-lg mt-3">
            <div className="row justify-content-center">
                <div className="col-8">
                    {currentmode === 'AddProduct' ? <AddProduct /> : null}
                    {currentmode === 'Orders' ? <Orders /> : null}
                    {currentmode === 'MyLists' ? <MyLists /> : null}
                    {currentmode === 'Opininos' ? <Opininos /> : null}
                    {currentmode === 'Account' ? <Account /> : null}
                    {currentmode === 'Logout' ? <Logout currentmode = {currentmode} /> : null}

                </div>
                <div className="col-4 border ">
                    <div className="p-2">
                        <p className="lead text-center mt-3">عزیز خوش آمدید {user.name}</p>
                        <ul className="list-group text-center">
                            <li style={{borderColor : currentmode === 'AddProduct' ? 'red' : null}} onClick={() => setCurrentmode('AddProduct')} className="list-item btn btn-outline-secondary mt-4 ">افزودن محصول </li>
                            <li style={{borderColor : currentmode === 'Orders' ? 'red' : null}} onClick={() => setCurrentmode('Orders')} className="list-item btn btn-outline-secondary mt-4">سفارش ها </li>
                            <li style={{borderColor : currentmode === 'MyLists' ? 'red' : null}} onClick={() => setCurrentmode('MyLists')} className="list-item btn btn-outline-secondary mt-4">لیست های من</li>
                            <li style={{borderColor : currentmode === 'Opininos' ? 'red' : null}} onClick={() => setCurrentmode('Opininos')} className="list-item btn btn-outline-secondary mt-4">دیدگاه ها</li>
                            <li style={{borderColor : currentmode === 'Account' ? 'red' : null}} onClick={() => setCurrentmode('Account')} className="list-item btn btn-outline-secondary mt-4">اطلاعات حساب کاربری</li>
                            <li style={{borderColor : currentmode === 'Logout' ? 'red' : null}} onClick={() => setCurrentmode('Logout')} className="list-item btn btn-outline-secondary mt-4">خروج از حساب کاربری</li>

                        </ul>
                    </div>
                </div>
            </div>
        </div>) : <Navigate to="/login" />}
        </div>
     );
}
 
export default PersonalPage;
