import { useState } from "react";
import { Link } from "react-router-dom";
import {UseLogin} from "../hooks/UseLogin"

const Login = () => {
    
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const {error,isloading,login} = UseLogin()

    const pressHandler = async(e) => {
        e.preventDefault()
        await login(email,password)
    }

    return ( 
        <div className="container-lg">
            <div className="row justify-content-center align-items-center">
                <div className="col-3">
                    <form onSubmit={pressHandler} className="mt-5">
                    <label className="form-label mt-3 lead">Email </label>
                        <div className="input-group">
                            <span className="input-group-text"><i className="bi bi-envelope"></i></span>
                            <input type="email" 
                        className="form-control" 
                        placeholder="e.g.test@gmail.com"
                        required
                        value={email}
                        onChange={(e)=>setEmail(e.target.value)}
                        />
                        </div>

                        <label className="form-label mt-3 lead">Password </label>
                        <div className="input-group">
                            <span className="input-group-text"><i className="bi bi-lock"></i></span>
                            <input type="password" 
                                className="form-control" 
                                placeholder="e.g. #242231dajd@"
                                required
                                value={password}
                                onChange={(e)=>setPassword(e.target.value)}
                            />
                            <span className="input-group-text"><i class="bi bi-eye"></i></span>
                            
                          

                        </div>

                        
                        <button disabled={isloading} className="btn btn-lg btn-primary my-3">{isloading ? "Logging in ..." : "Login"}</button>
                        {error && <div className="my-3 text-danger">{error}</div>}
                        <Link className="d-block fs-5" to="/signup">havent signup yet ? signup now</Link>


                    </form>
                </div>
            </div>
        </div>
     );
}
 
export default Login;
