import { Link, useParams } from "react-router-dom";
import { useState , useEffect} from "react";
import { UseAuthContext } from "../hooks/UseAuthContext";
import pic from '../assets/2.jpg';

const Details = () => {

    const {id} = useParams()

    const[product,setProduct] = useState(null)
    const [isowner,setIsowner] = useState(null)
    const [error,setError] = useState(null)
    const {user} = UseAuthContext()
    const [isloading,setIsloadig] = useState(true)
    
    useEffect(()=>{
        const getProduct = async () => {
            const response = await fetch(`/api/products/${id}`,{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                setProduct(json)
                setIsloadig(false)
            }
            if (json.user_id == user.user_id){
                setIsowner(true)
            }else{
                setIsowner(false)
            }
        }
            getProduct()
    },[user])

    return ( 
        <div className="container-lg bg-light">
            {isloading && (
                <div className="row justify-content-center p-5">
                    <div className="col-9">
                        <p>در حال بارگذاری محصول</p>
                    </div>
                </div>
            )}
            {!isloading && (
                            <div className="row justify-content-center p-5">
                            <div className="col-5">
                                <p>اطلاعات محصول با نام : {product ? `${product.title}` : null}  </p>
                                {product && (
                                    <div className="card">
                                <div className="card-header"><img src={pic} style={{width : "100%" , height : "100%"}} /></div>
                                        <div className="card-header text-center">{product.title}</div>
                                        <div className="card-body">
                                            <div className="card-title text-end">دسته بندی : {product.category}</div>
                                            <div className="card-subtitle">
                                                <p className="text-end mt-2">اینستاگرام : {product.instagram}</p>
                                                <p className="text-end ">تلگرام : {product.telegram}</p>

                                            </div>
                                        </div>
                                        {isowner && (
                                            <div className="card-text text-end"> 
                                                <p className="lead ">شما صاحب این محصول هستید جهت ویرایش یا حذف محصول از کلید زیر استفاده کنید</p>
                                                <Link to={`/editproduct/${product._id}`} className="btn btn-success">ویرایش محصول </Link>
                                            </div>
                                        )}
                                        {!isowner && (
                                            <div className="card-text">
                                                <p>در صورت تمایل برای خرید یا موارد مشابه میتوانید با صاحب محصول ارتباط برقرار کنید</p>
                                                <button className="btn btn-secondary">ارتباط با صاحب محصول</button>
                                            </div>
                                        )}
                                    </div>
                                )}
            
                            </div>
                        </div>
            )}

            
        </div>
     );
}
 
export default Details;
