import { Link, useParams } from "react-router-dom";
import { useState , useEffect} from "react";
import { UseAuthContext } from "../hooks/UseAuthContext";
import pic from '../assets/2.jpg';
import Loading from "../components/Loading";

const Details = () => {

    const {id} = useParams()

    const[product,setProduct] = useState(null)
    const [isowner,setIsowner] = useState(null)

    const {user} = UseAuthContext()
    const [isloading,setIsloadig] = useState(true)
  
    const {error,addintresting,AddIntresting,RemoveIntresting} = useLike()

    const addlike = async (id) => {
        await AddIntresting(id)
    }
    // const AddIntresting = async(id) => {
    //     setAddintresting(true)
    //     const response = await fetch(`/api/products/interests/${id}`,{
    //         method : 'PATCH',
    //         headers : {
    //             'Authorization' : `Bearer ${user.token}`
    //         }
    //     })
    //     if(!response.ok){
    //         setError("شما این محصول را لایک کرده اید")
    //         setTimeout(()=>{
    //             setError(null)
    //         },3000)
    //         setAddintresting(false)
    //     }
    //     if(response.ok){
    //         setAddintresting(false)
    //     }

        
    // }

    useEffect(()=>{
        const getProduct = async () => {
            const response = await fetch(`/api/products/${id}`,{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                setProduct(json)
                setIsloadig(false)
            }
            if (json.user_id == user.user_id){
                setIsowner(true)
            }else{
                setIsowner(false)
            }
        }
            getProduct()
    },[user])

    return ( 
        <div >
            {isloading && <Loading />}
            {!isloading && (
                <div className="container-lg bg-light">
                     <div className="row justify-content-center p-5">
                       <div className="col-6">
                    
                       {product && (
                            <div className="card">
                  <div className="card-header"><img src={pic} style={{width : "100%" , height : "100%"}} /></div>
                         <div className="card-header text-center">{product.title}</div>
                        <div className="card-body">
                     <div className="card-title text-end">دسته بندی : {product.category}</div>
                        <div className="card-subtitle text-end">
                                   {product.instagram} : اینستاگرام
                                <br />
                                    {product.telegram} : تلگرام
                               </div>
                          <div className="card-text text-end">{product.about}</div>
                        </div>
                            {isowner && (
                               <div className="card-text text-end"> 
                                    <p className="lead px-3">            
                            شما صاحب این محصول هستید جهت ویرایش یا حذف محصول از کلید زیر استفاده کنید</p>
                                   <div className="row justify-content-center">
                                         <div className="col-lg-3 col-10">
                                          <Link to='/products' className="btn btn-secondary btn-lg">بازگشت <i className="bi bi-arrow-return-left"></i></Link>
                                         </div>
                                        <div className="col-3">
                                        <button disabled={addintresting} 
                                             onClick={() => addlike(product._id)} 
                                             className="btn btn-outline-danger me-2 mb-2"> 
                                                <i className="bi bi-heart"></i>
                                        </button>
                                     </div>
                          <div className="col-3">
                          <Link to={`/editproduct/${product._id}`} className="btn btn-success me-2 mb-2 btn-lg">ویرایش <i className="bi bi-pencil-fill"></i></Link>
                        </div>
            </div>
                </div>
                   )}
                      {!isowner && (
                 <div className="card-text text-end">
                   <p className="lead px-3">در صورت تمایل برای خرید یا موارد مشابه میتوانید با صاحب محصول ارتباط برقرار کنید</p>
                      <div className="row justify-content-between">
                         <div className="col-lg-3 col-10">
                    <Link to='/products' className="btn btn-secondary btn-lg">بازگشت <i className="bi bi-arrow-return-left"></i></Link>
                                 </div>
                           <div className="col-3">
                           <button disabled={addintresting} 
                                onClick={() => addlike(product._id)} 
                                className="btn btn-outline-danger me-2 mb-2"> 
                                    <i className="bi bi-heart"></i>
                           </button>

                             </div>
                                 <div className="col-3">
                    <button className="btn btn-secondary me-2 mb-2 btn-lg">خرید <i className="bi bi-bag-check"></i></button>
                               </div>
                    </div>
                   </div>
                                        )}
                                  {error && (
                                            <div className="bg-warning my-2 text-center">
                                                <p className="p-3 text-secondary lead">{error}</p>
                                            </div>
                                            
                                        )}
            </div>
             )}
   
       </div>
        </div>
                                                    
                        </div>
            )}

            
        </div>
     );
}
 
export default Details;
