import { useState } from "react";
import { UseAuthContext } from "../hooks/UseAuthContext";

const Delete = ({id,setCurrentmode}) => {

    const {user} = UseAuthContext()
    const [isloading,setIsloading] = useState(false)
    const [error,setError] = useState(null)

    const deleteProduct = async () => {
        setIsloading(true)
        const response = await fetch(`/api/products/${id}`,{
            method : 'DELETE',
            headers : {
                'Authorization' : `Bearer ${user.token}`
            }
        })
        const json = await response.json()
        if (!response.ok){
            setIsloading(false)
            setError(json.error)
        }
        if (response.ok){
            setIsloading(false)
            setCurrentmode('Edit')
        }

    }

    return ( 
        <div className="container border ">
        <h5 className="text-end pt-3 pe-3">حذف محصول </h5>
        <div className="row justify-content-center align-items-center">
           <div className="col-8">
               <p className='pt-5 h5 mb-5'>آیا از حذف این محصول اطمینان دارید ؟ </p>
               <div className="row">
                   <div className="col-6 pb-5">
                       <button onClick={() => setCurrentmode('Edit')} className="btn btn-lg btn-outline-success" >خیر </button>                 
                   </div>
                   <div className="col-6">
                       <button disabled={isloading} className="btn btn-lg btn-outline-danger" onClick={deleteProduct}>{isloading ? 'در حال حذف' : 'حذف'}</button>    
                   </div>
                 
               
               </div>
           </div>

        </div>
   </div>
     );
}
 
export default Delete;
