import { useEffect, useState } from "react";
import Loading from '../Loading'
import {UseAuthContext} from '../../hooks/UseAuthContext'
import picture from '../../assets/2.jpg'
const Products = () => {

    const [products,setProducts] = useState(null)
    const [isloading,setIsloading] = useState(true)
    const {user} = UseAuthContext()

    useEffect(()=>{
        const getProducts = async()=>{

            const response = await fetch('/api/admin/products',{
                headers : {
                    'Authorization'  : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if(response.ok){
                setProducts(json)
                setIsloading(false)
            }
        }
            getProducts()
    },[])

    return ( 
        <div className="container border border-primary">
            <p className="text-end pt-2 pe-2">لیست محصولات</p>
            {isloading ?

                <Loading />

                :

                <div className="row justify-content-center">
                    {products && products.map((p) => (
                        <div className="col-12 col-lg-9" key={p._id}>
                            <div className="card mb-3">
                                <div className="card-header">
                                    <img src={picture} alt="Photo" style={{width : '100%' , height : '100%'}}/>
                                </div>
                                <div className="card-header text-center">
                                    {p.title} 
                                </div>
                                <div className="card-subtitle">
                                    {p.price} : قیمت
                                </div>
                                <div className="card-text">
                                    {p.instagram} <br/> {p.telegram} <br /> {p.about} <br /> 
                                    {(p.interests).lenght} <i className="bi bi-heart-fill"></i>
                                </div>

                            </div>
                        </div>
                    ))}
                </div>

            }
        </div>
     );
}
 
export default Products;
