import { useState } from "react";
import { Link } from "react-router-dom";

const Signup = () => {
    const [name,setName] = useState('')
    const [lastname,setLastName] = useState('')
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')

    const pressHandler = (e) => {
        e.preventDefault()
        console.log(name,lastname,email,password)
    }
    return ( 
        <div className="container-lg">
            <div className="row justify-content-center align-items-center">
                <div className="col-10 col-log-6">
                    <form onSubmit={pressHandler} className="mt-5">
                        <label className="form-label lead">Name</label>
                        <div className="input-group">
                            <span className="input-group-text"><i class="bi bi-person-fill"></i></span>
                            <input type="text" 
                                className="form-control" 
                                placeholder="e.g. Amir"
                                required
                                value={name}
                                onChange={(e)=>setName(e.target.value)}
                            />
                        </div>

                        <label className="form-label mt-3 lead">LastName</label>
                        <div className="input-group">
                            <span className="input-group-text"><i class="bi bi-person-fill"></i></span>
                            <input type="text" 
                                className="form-control" 
                                placeholder="e.g. Ahmadi"
                                required
                                value={lastname}
                                onChange={(e)=>setLastName(e.target.value)}
                            />
                        </div>

                        <label className="form-label mt-3 lead">Email </label>
                        <div className="input-group">
                            <span className="input-group-text"><i class="bi bi-envelope"></i></span>
                            <input type="email" 
                        className="form-control" 
                        placeholder="e.g.test@gmail.com"
                        required
                        value={email}
                        onChange={(e)=>setEmail(e.target.value)}
                        />
                        </div>

                        <label className="form-label mt-3 lead">Password </label>
                        <div className="input-group">
                            <span className="input-group-text"><i class="bi bi-lock"></i></span>
                            <input type="password" 
                                className="form-control" 
                                placeholder="e.g. #242231dajd@"
                                required
                                value={password}
                                onChange={(e)=>setPassword(e.target.value)}
                            />
                        </div>
                       

                        <button className="btn btn-lg btn-primary my-3">Signup</button>
                        <Link className="d-block fs-5" to="/login">Already have an acoount ? login</Link>

                    </form>
                </div>
            </div>
        </div>
     );
}
 
export default Signup;
