import { UseAuthContext } from "../hooks/UseAuthContext";
import { useState } from "react";
import { UseLogout } from '../hooks/UseLogout'

// import components

import Account from "../components/Account";
import AddProduct from "../components/AddProduct";
import Opininos from "../components/Opininos";
import Orders from "../components/Orders";




const PersonalPage = () => {
    const {user} = UseAuthContext()
    const [currentmode,setCurrentmode] = useState('Add')
    const { logout } = UseLogout()
    
    const logoutHandler = () => {
        logout()
    }

    return ( 
        <div className="container-lg mt-3">
            <div className="row justify-content-center">
                <div className="col-8">
                    <h1>Add Product</h1>
                </div>
                <div className="col-4 border ">
                    <div className="p-2">
                        <p className="lead text-center mt-3">عزیز خوش آمدید {user.name}</p>
                        <ul className="list-group text-center">
                            <li onClick={() => setCurrentmode('Add')} className="list-item btn btn-outline-secondary mt-4">افزودن محصول </li>
                            <li onClick={() => setCurrentmode('orders')} className="list-item btn btn-outline-secondary mt-4">سفارش ها </li>
                            <li onClick={() => setCurrentmode('lists')} className="list-item btn btn-outline-secondary mt-4">لیست های من</li>
                            <li onClick={() => setCurrentmode('opinions')} className="list-item btn btn-outline-secondary mt-4">دیدگاه ها</li>
                            <li onClick={() => setCurrentmode('account')} className="list-item btn btn-outline-secondary mt-4">اطلاعات حساب کاربری</li>
                            <li onClick={logoutHandler} className="list-item btn btn-outline-secondary my-4">خروج از حساب کاربری</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
     );
}
 
export default PersonalPage;
