import { useState } from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import {UseAuthContext} from '../hooks/UseAuthContext'
import {UseProductContext} from '../hooks/UseProductContext'
import EditProduct from '../components/EditProduct'

const MyProducts = () => {
    
    const {user} = UseAuthContext()
    
    const [ischanging,setIschanging] = useState(false)
    const [projectId,setProjectId] = useState(null)
    const {userProducts} = UseProductContext()

    const EditProduct = (id) => {
        setProjectId(id)
        setIschanging(true)
    }

   
    return ( 
        <div className="products">
            {ischanging && <EditProduct id={projectId} setIschanging ={setIschanging} />}
            {!ischanging && (
                <div className="container border">
                <h5 className="text-end pt-3 pe-3">محصولات های من </h5>
                
                {userProducts && userProducts.map((p)=>(
                    <div className="row justify-content-center" key={p._id}>
                        <div className="col-12 col-lg-8 my-3 ">
                            <div className="card">
                                <div className="card-header">{p.title}</div>
                                <div className="card-body">
                                    <div className="card-title">{p.user_id}</div>
                                        <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                        <div className="card-text">{p.about} </div>
                                        <div className="mt-3">
                                            <button onClick={() => EditProduct(p._id)} className="btn btn-secondary me-2">Edit</button>
                                        </div>
    
                                    </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
                )}
        </div>
        
        
     );
}
 
export default MyProducts;
