import { useState,useEffect } from "react";
import Loading from './Loading'
import {UseProductContext} from '../hooks/UseProductContext'
import pic from '../assets/2.jpg';
import { Link } from "react-router-dom";
import { UseAuthContext } from "../hooks/UseAuthContext";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const Opininos = () => { 

    const [isloading,setIsloading] = useState(true)
    const {comments,dispatch} = UseProductContext()
    const {user} = UseAuthContext()
    useEffect(()=>{
        const getUserComments = async () => {
            
            const response = await fetch('/api/products/comment',{
                method : 'POST',
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()
    
            if (response.ok) {
                dispatch({type : "SET_COMMENTS" , payload : json})
                setIsloading(false)
            }

            getUserComments()
        }
    },[])
    return ( 
        <div className="container border border-primary">
            <h5 className="text-end pt-3 pe-3">دیدگاه های من  </h5>
            {
                isloading 
                ?

                <Loading />
                
                :

                <div> 
                    {comments && comments.map((p)=>(
                        <div className="row justify-content-center">
                            <div className="col-12 col-md-8 my-3 " key={p._id}>
                                <div className="card">
                                    <div className="card-header"><img src={pic} style={{width : "100%" , height : "100%"}} /></div>
                                    <div className="card-body">
                                        <div className="card-title text-end">{p.title}</div>
                                        <div className="card-subtitle text-end my-2">قیمت : {p.price} هزار تومان</div>
                                         <div className="card-text text-end mb-2">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>

                                            <div className="text-center">
                                                <button disabled={addintresting} 
                                                    onClick={() => RemoveIntresting(p._id)} 
                                                    className="btn btn-outline-danger me-2 mb-2"> 
                                                    <i class="bi bi-heart-fill"></i>
                                                </button>
                                                <Link to={`/details/${p._id}`} className="btn btn-lg btn-secondary me-2">مشاهده محصول </Link>
                                            </div>
                                        {error && (
                                            <div className="bg-danger my-2">
                                                <p className="p-3 text-secondary lead">{error}</p>
                                            </div>
                                        )}

                                        </div>
                                    </div>
                                </div>
                            </div>
                                ))}
                </div>
            }
        </div>
     );
}
 
export default Opininos;
