import { useState, useEffect } from "react";

import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { UseAuthContext } from "../hooks/UseAuthContext";
import { Link } from "react-router-dom";
import pic from '../assets/2.jpg';
import Loading from "../components/Loading";

const Products = () => {
    const [products,setProducts] = useState()
    const {user} = UseAuthContext()
    const[userid,setUserid] =  useState(null)
    const [isloading,setIsloading] = useState(true)

    useEffect(()=>{
        const getProducts = async () => {

            const response = await fetch('/api/products/')
            const json = await response.json()

            if (response.ok){
            setProducts(json)
                setIsloading(false)
            }
        }   
        getProducts()
        if(user){
            setUserid(user.user_id)
        }

    },[user])


    return ( 
        <div>
            <div className="bg-dark">
                <p className="text-center welcome">به صفحه محصولات خوش آمدید</p>
            </div>
            <div className="container-lg">
            <div className="row justify-content-center mt-5">
                {isloading && <Loading />}
                {!isloading && (
                    <div className="row justify-content-center">
                    {products && products.map((p)=>(
                    
                
                        <div className="col-12 col-lg-4 my-3 " key={p._id}>
                            <div className="card">
                                <div className="card-header"><img src={pic} alt='image' style={{width : "100%" , height : "100%"}} /></div>
                                <div className="card-body">
                                    <div className="card-title text-end">{p.title}  : <span className="lead">عنوان</span> </div>
                                    <div className="card-subtitle text-end my-2"><span className="lead">قیمت</span> : {p.price} هزار تومان</div>
                                    <div className="card-text text-end mb-2"> {formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })} : منتشر شده در </div>
                                   { p.createdAt !== p.updatedAt

                                        ? 
                                   
                                   <div className="card-text text-end mb-2"> 
                                        {formatDistanceToNow(new Date(p.updatedAt), { addSuffix: true })} : ویرایش شده در 
                                    </div>
                                   
                                        :
                                   
                                        null
                                   } 
                                   
                                    <div className="card-text text-end fs-4">
                                        <i className="bi bi-heart-fill text-danger"></i> {(p.interests).length}
                                    </div>
                                    <div className="text-center">
                                        { userid !== p.user_id && 
                                        <Link to={user ? `/details/${p._id}` : '/signup'} 
                                            className="btn btn-primary fw-btn my-2">
                                                {user ? "مشاهده محصول " : "  (ساخت حساب )برای مشاهده باید اکانت بسازید"}
                                        </Link>
                                        }
                                        { userid === p.user_id && 
                                        <Link to={user ? `/details/${p._id}` : '/signup'} 
                                            className="btn btn-secondary my-2 fw-btn">
                                            محصول شما - مشاهده و ویرایش
                                        </Link>
                                        } 
                                    </div>

                                </div>
                            </div>
                        </div>
                    

                ))}
                    </div>
                )}
            
            </div>
        </div>
        </div>

      
     );
}
 
export default Products;
