import { useState, useEffect } from "react";
import {UseProductContext} from '../hooks/UseProductContext'
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { UseAuthContext } from "../hooks/UseAuthContext";
import { Link } from "react-router-dom";

const Products = () => {
    const {products} = UseProductContext()
    const {user} = UseAuthContext()
    const[userid,setUserid] =  useState(null)
    useEffect(()=>{
        if(user){
            setUserid(user.user_id)
        }
    },[])


    return ( 
        <div className="container-lg">
            <div className="row justify-content-center mt-5">
                {products && products.map((p)=>(
                    
                    <div className="row justify-content-center" key={p._id}>
                        <div className="col-12 col-lg-8 my-3 ">
                            <div className="card">
                                <div className="card-header">{p.title}</div>
                                <div className="card-body">
                                    <div className="card-title">{p.user_id}</div>
                                    <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                    <div className="card-text">{p.about} </div>
                                     { userid !== p.user_id && <Link to={`/details/${p._id}`} className="btn btn-primary  my-2">مشاهده محصول</Link>}
                                    { user.user_id == p.user_id && <Link to={`/details/${p._id}`} className="btn btn-secondary my-2">محصول شما - مشاهده و ویرایش</Link>} 
                                </div>
                            </div>
                        </div>
                    </div>

                ))}
            
            </div>
        </div>
     );
}
 
export default Products;
