import {UseAuthContext} from '../hooks/UseAuthContext'

const EditProduct = ({id}) => {
    const {user} = UseAuthContext()
    const [error,setError] = useState(null)
    const [product,setProduct] = useState(null)

    useEffect(()=>{
        const userProduct = async () => {
            const response = await fetch(`/api/products/userproducts/${id}`,{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if (!response.ok){
                setError(json.error)
            }
            if (response.ok){
                setProduct(json)
            }
            
        }
        if(user){
            userProduct()
        }
           
    },[user])

    return ( 
        <div className="container border">
            <h5 className="text-end pt-3 pe-3">محصولات من</h5>
            {product && (
                    <div className="row justify-content-center">
                        <div className="col-12 col-lg-8 my-3 ">
                            <div className="card">
                                <div className="card-header">{product.title}</div>
                                <div className="card-body">
                                    <div className="card-title">{product.user_id}</div>
                                        <div className="card-subtitle">{formatDistanceToNow(new Date(product.createdAt), { addSuffix: true })}</div>
                                        <div className="card-text">{product.about} </div>
                                        <div className="mt-3">
                                            <button onClick={RemoveHandler} className="btn btn-danger me-2">Reomve</button>
                                            <button onClick={UpdateHandler} className="btn btn-warning">Update</button>
                                            <button onClick={() => setIschanging(false)} className="btn btn-warning">Back to products</button>
                                        </div>
    
                                    </div>
                            </div>
                        </div>
                    </div>
                )}
        </div>
     );
}
 
export default EditProduct;
