import {UseAuthContext} from '../hooks/UseAuthContext'


const Account = () => {
    const {user} = UseAuthContext()

    return ( 
        <div className="container border">
            <h5 className="text-end pt-3 pe-3">حساب کاربری من </h5>
            <p className='text-end lead mt-3'>{user.name} : نام  </p>
            <p className='text-end lead'> {user.lastname} : نام خانوادگی </p>
            <p className='text-end lead'> {user.email} : ایمیل </p>
            <button>ویرایش حساب کاربری</button>
        </div>
     );
}
 
export default Account;
