import { createContext ,useReducer } from "react";
import {UseAuthContext} from './AuthContext'

export const ProductContext = createContext()

export const productReduces = (state,action) =>{
    switch(action.type){
        case 'SET_PRODUCTS' :
            return {
                products : action.payload
            }
        case 'SET_USER_PRODUCTS' :
            return {
                userProducts : action.payload
            }
        case 'CREATE_USER_PRODUCT' :
            return {
                userProducts : [action.payload , ...state.products]
            }
        case 'DELETE_USER_PRODUCT':
            return {
                userProducts : state.products.filter((p) => p._id !== action.payload._id)
            }
        case 'UPDATE_USER_PRODUCT':
            return {
                userProducts : state.products.filter((p) => p._id !== action.payload._id)
            }
        default: 
            return state
    }
}

export const ProductContextProvider = ({children}) => {
    
    const {user} = UseAuthContext()

    const [state,dispatch] = useReducer(productReduces,{
        products : null,
        userProducts : null,
    })
    useEffect(()=>{
        const getProducts = async () => {
            const response = await fetch('/api/products')
            const json = await response.json()
            if (response.ok){
                dispatch({type : "SET_PRODUCTS",payload : json})
            }
        }
        const getUserProducts = async () => {
            const response = await fetch('/api/products/userproducts',{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()
            if (response.ok){
                dispatch({type : "SET_USER_PRODUCTS",payload : json})
            }
        }

        getProducts()
        if (user){
            getUserProducts()
        }
    },[dispatch])

    return (
        <ProductContext.Provider value={{...state,dispatch}}>
            {children}
        </ProductContext.Provider>
    )
}
