import { useState ,useEffect } from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { UseAuthContext } from "../hooks/UseAuthContext";

import EditProduct from '../components/EditProduct'

const MyProducts = () => {
    
    
    
    const [ischanging,setIschanging] = useState(false)
    const [project,setProject] = useState(null)
    
    const [error,setError] = useState(null)
    const [products,setProducts] = useState(null)
    const {user} = UseAuthContext()

    const EditProduct = (title,about,price,category,number,instagram,telegram,user_id) => {
        setProject({title,about,price,category,number,instagram,telegram,user_id})
        setIschanging(true)
    }

    useEffect(()=>{
        const getUserProducts = async () => {
            const response = await fetch('/api/products/userproducts',{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()
            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                setProducts(json)
            }
        }
        if (user){
            getUserProducts()
        }
    },[user])

    return ( 
        <div className="products">
            {ischanging && <EditProduct project={project} setIschanging ={setIschanging} />}
            {!ischanging && (
                <div className="container border">
                <h5 className="text-end pt-3 pe-3">محصولات های من </h5>
                {error && <div>{error}</div>}
                {products && products.map((p)=>(
                    <div className="row justify-content-center" key={p._id}>
                        <div className="col-12 col-lg-8 my-3 ">
                            <div className="card">
                                <div className="card-header">{p.title}</div>
                                <div className="card-body">
                                    <div className="card-title">{p.user_id}</div>
                                        <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                        <div className="card-text">{p.about} </div>
                                        <div className="mt-3">
                                            <button onClick={() => EditProduct(p.title,p.about,p.price,p.category,p.number,p.instagram,p.telegram,p.user_id)} className="btn btn-secondary me-2">Edit</button>
                                        </div>
    
                                    </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
                )}
        </div>
        
        
     );
}
 
export default MyProducts;
