import { Link, useParams } from "react-router-dom";
import { useState , useEffect} from "react";
import { UseAuthContext } from "../hooks/UseAuthContext";
import pic from '../assets/2.jpg';
import Loading from "../components/Loading";


const Details = () => {

    const {id} = useParams()

    const[product,setProduct] = useState(null)
    const [isowner,setIsowner] = useState(null)
    const [addintresting,setAddintresting] = useState(false)

    const {user} = UseAuthContext()
    const [isloading,setIsloadig] = useState(true)
    const [error,setError] = useState(null)
    const [isliked,setIsliked] = useState(null)
    const [likes,setLikes] = useState(null)
    const [comment,setComment] = useState(null)
    const [comerror,setComerror] = useState(null)
    const [comloading,setComloading] = useState(false)

    const PostComment = async () => {
        setComloading(true)
        const response = await fetch(`/api/products/comment/${id}`,{
            method : 'POST',
            headers : {
                "Content-Type" : "application/json",
                'Authorization' : `Bearer ${user.token}`
            },
            body : JSON.stringify({comment})
        })
        const json = await response.json()

        if (!response.ok){
            setComloading(false)
            setComerror(json.error)
            setTimeout(()=>{
                setComerror(null)
            },2000)
        }
        if (response.ok) {
            setComloading(false)

        }
    }

    const AddIntresting = async(id) => {
        setAddintresting(true)
        const response = await fetch(`/api/products/interests/${id}`,{
            method : 'PATCH',
            headers : {
                'Authorization' : `Bearer ${user.token}`
            }
        })
        const json = await response.json()
        if(!response.ok){
            setError("شما این محصول را لایک کرده اید")
            setTimeout(()=>{
                setError(null)
            },3000)
            setAddintresting(false)
        }
        if(response.ok){
            setLikes((json.interests).length)
            setIsliked(true)
            setAddintresting(false)
        }

        
    }
    const RemoveIntresting = async(id) => {
        setAddintresting(true)
        const response = await fetch(`/api/products/interests/${id}`,{
            method : 'DELETE',
            headers : {
                'Authorization' : `Bearer ${user.token}`
            }
        })
        const json = await response.json()
        
        if (!response.ok){
            setError('شما این محصول را لایک نکرده اید ')
        }
        if (response.ok){
            setLikes((json.interests).length)
            setIsliked(false)
            setAddintresting(false)
        }

    }
    useEffect(()=>{
        const getProduct = async () => {
            const response = await fetch(`/api/products/${id}`,{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()

            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                if (json.interests.includes(user.user_id)){
                    setIsliked(true)
                }
                setLikes((json.interests).length)
                setProduct(json)
                setIsloadig(false)
            }
            if (json.user_id === user.user_id){
                setIsowner(true)
            }else{
                setIsowner(false)
            }
        }
            getProduct()
    },[user,id])

    return ( 
        <div >
            {isloading && <Loading />}
            {!isloading && (
                <div className="container-lg bg-light">
                     <div className="row justify-content-center p-5">
                       <div className="col-6">
                    
                       {product && (
                            <div className="card">
                  <div className="card-header"><img src={pic} alt='picture' style={{width : "100%" , height : "100%"}} /></div>
                         <div className="card-header text-center">{product.title}</div>
                        <div className="card-body">
                     <div className="card-title text-end">دسته بندی : {product.category}</div>
                        <div className="card-subtitle text-end">
                                   {product.instagram} : اینستاگرام
                                <br />
                                    {product.telegram} : تلگرام
                               </div>
                          <div className="card-text text-end">{product.about}</div>
                          

                <div className="input-group  mt-3">
                    <span className="input-group-text">
                        <i className="bi bi-pencil-square"></i>
                    </span>
                    <input 
                        type="text" 
                        value={comment}
                        maxLength = {50}
                        onChange={(e)=>setComment(e.target.value)}
                        className="form-control text-end" 
                        placeholder="نطر خود را به اشتراک بگذارید " />
                </div>

                { comment && (
                    <div>
                        <button
                            disabled ={comloading}
                            onClick={PostComment} 
                            className="btn btn-outline-success mt-3 fw-btn">
                                {comloading ? "در حال انتشار" : "انتشار نظر "} 
                        </button>
                        {comerror && (
                            <div className="border border-2 border-danger mt-2 text-center">
                                <p className="p-1">{comerror}</p>
                            </div>
                        )}
                    </div>
                )}
                        </div>
                            {isowner && (
                               <div className="card-text text-end"> 
                                    <p className="lead px-3">            
                            شما صاحب این محصول هستید جهت ویرایش یا حذف محصول از کلید زیر استفاده کنید</p>
                                   <div className="row justify-content-center">
                                         <div className="col-lg-3 col-10">
                                          <Link to='/products' className="btn btn-secondary btn-lg">بازگشت <i className="bi bi-arrow-return-left"></i></Link>
                                         </div>
                                        <div className="col-3">
                                        <button disabled={addintresting} 
                                             onClick={isliked ? () => RemoveIntresting(product._id)  : () => AddIntresting(product._id)} 
                                             className="btn btn-outline-danger me-2 mb-2"> 
                                                {isliked 
                                                ? 
                                                    <span><i class="bi bi-heart-fill"></i> {likes}</span>
                                                :
                                                  <span><i className="bi bi-heart"></i> {likes}</span> 
                                                }
                                        </button>
                                     </div>
                          <div className="col-3">
                          <Link to={`/editproduct/${product._id}`} className="btn btn-success me-2 mb-2 btn-lg">ویرایش <i className="bi bi-pencil-fill"></i></Link>
                        </div>
            </div>
                </div>
                   )}
                      {!isowner && (
                 <div className="card-text text-end">
                   <p className="lead px-3">در صورت تمایل برای خرید یا موارد مشابه میتوانید با صاحب محصول ارتباط برقرار کنید</p>
                      <div className="row justify-content-between">
                         <div className="col-lg-3 col-10">
                    <Link to='/products' className="btn btn-secondary btn-lg">بازگشت <i className="bi bi-arrow-return-left"></i></Link>
                                 </div>
                           <div className="col-3">
                           <button disabled={addintresting} 
                                 onClick={isliked ? () => RemoveIntresting(product._id)  : () => AddIntresting(product._id)} 
                                 className="btn btn-outline-danger me-2 mb-2"> 
                                    {isliked 
                                        ? 
                                         <span><i class="bi bi-heart-fill"></i> {likes}</span>
                                        :
                                          <span><i className="bi bi-heart"></i> {likes}</span> 
                                    }
                            </button>

                             </div>
                                 <div className="col-3">
                    <button className="btn btn-secondary me-2 mb-2 btn-lg">خرید <i className="bi bi-bag-check"></i></button>
                               </div>
                    </div>
                   </div>
                                        )}



                                  {error && (
                                            <div className="bg-warning my-2 text-center">
                                                <p className="p-3 text-secondary lead">{error}</p>
                                            </div>
                                            
                                        )}
            </div>
             )}
   
       </div>
        </div>
                                                    
                        </div>
            )}

            
        </div>
     );
}
 
export default Details;
