import { UseAuthContext } from "../hooks/UseAuthContext";
import { useState } from "react";

import { Navigate } from 'react-router-dom'

// import components

import Account from "../components/Account";
import AddProduct from "../components/AddProduct";
import Opininos from "../components/Opinions";
import Orders from "../components/Orders";
import MyProducts from "../components/MyProducts";
import Logout from "../components/Logout";


const PersonalPage = () => {
    const {user} = UseAuthContext()
    const [currentmode,setCurrentmode] = useState('AddProduct')


    return ( 
      
              <div className="container-lg mt-3">
            <div className="row justify-content-center">
                <div className="col-8">
                    {currentmode === 'AddProduct' ? <AddProduct /> : null}
                    {currentmode === 'Orders' ? <Orders /> : null}
                    {currentmode === 'MyProducts' ? <MyProducts /> : null}
                    {currentmode === 'Opininos' ? <Opininos /> : null}
                    {currentmode === 'Account' ? <Account /> : null}
                    {currentmode === 'Logout' ? <Logout setCurrentmode = {setCurrentmode} /> : null}

                </div>
                <div className="col-4 border ">
                    <div className="p-2">
                        <p className="lead text-center mt-3">عزیز خوش آمدید {user.name}</p>
                        <ul className="list-group text-center">
                            <li style={{borderColor : currentmode === 'AddProduct' ? 'blue' : null , color : currentmode === 'AddProduct' ? 'blue' : null}} onClick={() => setCurrentmode('AddProduct')} className="list-item btn btn-outline-secondary mt-4 ">افزودن محصول </li>
                            <li style={{borderColor : currentmode === 'Orders' ? 'blue' : null,color : currentmode === 'Orders' ? 'blue' : null}} onClick={() => setCurrentmode('Orders')} className="list-item btn btn-outline-secondary mt-4">سفارش ها </li>
                            <li style={{borderColor : currentmode === 'MyProducts' ? 'blue' : null,color : currentmode === 'MyProducts' ? 'blue' : null}} onClick={() => setCurrentmode('MyProducts')} className="list-item btn btn-outline-secondary mt-4">محصولات های من</li>
                            <li style={{borderColor : currentmode === 'Opininos' ? 'blue' : null,color : currentmode === 'Opininos' ? 'blue' : null}} onClick={() => setCurrentmode('Opininos')} className="list-item btn btn-outline-secondary mt-4">دیدگاه ها</li>
                            <li style={{borderColor : currentmode === 'Account' ? 'blue' : null,color : currentmode === 'Account' ? 'blue' : null}} onClick={() => setCurrentmode('Account')} className="list-item btn btn-outline-secondary mt-4">اطلاعات حساب کاربری</li>
                            <li style={{borderColor : currentmode === 'Logout' ? 'blue' : null,color : currentmode === 'Logout' ? 'blue' : null}} onClick={() => setCurrentmode('Logout')} className="list-item btn btn-outline-secondary mt-4">خروج از حساب کاربری</li>

                        </ul>
                    </div>
                </div>
            </div>
        </div>
        
     );
}
 
export default PersonalPage;
