import { UseAuthContext } from "../hooks/UseAuthContext";
import { useState } from "react";
import { UseLogout } from '../hooks/UseLogout'

// import components

import Account from "../components/Account";
import AddProduct from "../components/AddProduct";
import Opininos from "../components/Opinions";
import Orders from "../components/Orders";
import MyLists from "../components/Mylists";



const PersonalPage = () => {
    const {user} = UseAuthContext()
    const [currentmode,setCurrentmode] = useState('AddProduct')
    const { logout } = UseLogout()
    
    const logoutHandler = () => {
        logout()
    }
    const pageHandler = () => {
        switch(currentmode){
            case 'AddProduct' :
                return <AddProduct />
            case 'Orders' :
                return <Orders />
            case 'MyLists' :
                return <MyLists />
            case 'Opininos' :
                return <Opininos />
            case 'Account' :
                return <Account />    
            default :
                return <AddProduct /> 
        }
    }

    return ( 
        <div className="container-lg mt-3">
            <div className="row justify-content-center">
                <div className="col-8">
                    {pageHandler}
                </div>
                <div className="col-4 border ">
                    <div className="p-2">
                        <p className="lead text-center mt-3">عزیز خوش آمدید {user.name}</p>
                        <ul className="list-group text-center">
                            <li onClick={() => setCurrentmode('AddProduct')} className="list-item btn btn-outline-secondary mt-4">افزودن محصول </li>
                            <li onClick={() => setCurrentmode('Orders')} className="list-item btn btn-outline-secondary mt-4">سفارش ها </li>
                            <li onClick={() => setCurrentmode('MyLists')} className="list-item btn btn-outline-secondary mt-4">لیست های من</li>
                            <li onClick={() => setCurrentmode('Opininos')} className="list-item btn btn-outline-secondary mt-4">دیدگاه ها</li>
                            <li onClick={() => setCurrentmode('Account')} className="list-item btn btn-outline-secondary mt-4">اطلاعات حساب کاربری</li>
                            <li onClick={logoutHandler} className="list-item btn btn-outline-secondary my-4">خروج از حساب کاربری</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
     );
}
 
export default PersonalPage;
