import { useState ,useEffect } from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { UseAuthContext } from "../hooks/UseAuthContext";
import { Link } from "react-router-dom";


const MyProducts = () => {
    
    const [error,setError] = useState(null)
    const [products,setProducts] = useState(null)
    const {user} = UseAuthContext()
    const [isloading,setIsloading] = useState(true)

    useEffect(()=>{
        const getUserProducts = async () => {
            const response = await fetch('/api/products/userproduct',{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()
            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                setProducts(json)
                setIsloading(false)
            }
        }
        if (user){
            getUserProducts()
        }
    },[user])

    return ( 
        <div className="products">
            {isloading && (
                <div className="container border">
                    <h5 className="text-end pt-3 pe-3">محصولات های من </h5>
                    <div className="row justify-content-center">
                        <div className="col-12 col-lg-8 my-3 ">
                            <p>در حال بارگذاری محصولات شما</p>
                        </div>
                    </div>
                </div>
            )}  
            {!isloading && (
                <div className="container border">
                    <h5 className="text-end pt-3 pe-3">محصولات های من </h5>
                        {error && <div>{error}</div>}
                        {products && products.map((p)=>(
                            <div className="row justify-content-center" key={p._id}>
                                <div className="col-12 col-lg-8 my-3 ">
                                    <div className="card">
                                        <div className="card-header">{p.title}</div>
                                        <div className="card-body">
                                            <div className="card-title">{p.user_id}</div>
                                            <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                            <div className="card-text">{p.about} </div>
                                            <div className="mt-3">
                                                <Link to={`/details/${p._id}`} className="btn btn-secondary me-2">ویرایش محصول </Link>
                                            </div>
                    
                                        </div>
                                        </div>
                                    </div>
                                </div>
                                ))}
                    </div>
            )}              
        </div>
        
        
     );
}
 
export default MyProducts;
