import {UseAuthContext} from '../hooks/UseAuthContext'
import { useState } from "react";
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import { useEffect } from 'react';


const EditProduct = ({projectid,setIschanging}) => {

    const [isowner,setIsowner] = useState(null)
    const {user} = UseAuthContext()
    const [error,setError] = useState(null)
    const[product,setProduct] = useState(null)

    useEffect(()=>{
        const getProduct = async () => {
            const response = await fetch(`/api/products/${projectid}`,{
                headers : {
                    'Authorization' : `Bearer ${user.token}`
                }
            })
            const json = await response.json()
            if (!response.ok){
                setError(json.error)
            }
            if(response.ok){
                setProduct(json)
            }
            if (json._id == projectid){
                setIsowner(true)
            }else{
                setIsowner(false)
            }
        }
        if (user){
            getProduct()

        }
    },[projectid])

    return ( 
        <div className="container border">
            <h5 className="text-end pt-3 pe-3">Test</h5>
            {product &&  (
                    <div className="row justify-content-center" >
                        <div className="col-12 col-lg-8 my-3 ">
                            <div className="card">
                                <div className="card-header">{p.title}</div>
                                <div className="card-body">
                                    <div className="card-title">{p.user_id}</div>
                                        <div className="card-subtitle">{formatDistanceToNow(new Date(p.createdAt), { addSuffix: true })}</div>
                                        <div className="card-text">{p.about} </div>
                                            <div className="mt-3">
                                                <button onClick={() => EditBtn(p._id)} className="btn btn-secondary me-2">Edit</button>
                                            </div>
                                        </div>
                            </div>
                        </div>
                    </div>
            )}
        </div>
     );
}
 
export default EditProduct;
